mapper.select:
SELECT BNO, CATE_CD, TITLE, WRITER, CONTENT, TAG, VIEW_CNT, REGDATE
  FROM CH_BOARD
 WHERE BNO = #{bno}

mapper.update:
UPDATE CH_BOARD
   SET CATE_CD = #{cateCd, jdbcType=NULL}, TITLE = #{title}, CONTENT = #{content}, TAG = #{tag}, REGDATE = now()
 WHERE BNO = #{bno}

UPDATE CH_BOARD
   SET VIEW_CNT = VIEW_CNT + 1
 WHERE BNO = #{bno}
